package shakemon;

import io.javalin.Javalin;
import shakemon.api.HttpHandlers;

public class Main {
    public static void main(String[] args) {
        var app = Javalin.create().start(7000);
        var handlers = new HttpHandlers();
        handlers.register(app);
    }
}
